import axios from 'axios';
import Layout from '../../components/MyLayout';

function sendPost(post:any) {
  axios.post(
    'https://simple-blog-api.crew.red/posts/',{
      title: post.title,
      body: post.body
    }
  );
}

const newPost = () => {
  function handleSubmith(e:any) {
    e.preventDefault();
    const post = {
      title: e.target.elements.title.value,
      body: e.target.elements.body.value
    }
    sendPost(post);
    e.target.elements.title.value = '';
    e.target.elements.body.value = '';
  }
  return <Layout>
     <h1>Create new post</h1>
     <form action="" method="post" onSubmit={(e)=>{handleSubmith(e)}}>
       <label htmlFor="title">
         Post Title 
         <br/>
         <input type="text" name="title"/>
       </label>
       <br/>
       <label htmlFor="body">
         Post body
         <br/>
         <textarea name="body" cols="30" rows="10" />
       </label>
       <br/>
       <button type="submit" >submith</button>
     </form>
  </Layout>
}


export default newPost;
