/* eslint-disable react/react-in-jsx-scope */
import Layout from '../../components/MyLayout.js';
import axios from 'axios';

interface Props {
    type: object;
    post: {
        type: object;
        title: string;
        body: string;
    };
}

interface Post {
    type: object;
    title: string;
    body: string;
}

const Post: Post = (props: Props) => {
    const { post } = props;
    return (
        <Layout>
            <h1>{post.title}</h1>
            <p>{post.body}</p>
        </Layout>
    );
};

Post.getInitialProps = async function(context: any) {
    const { id } = context.query;
    const res = await axios.get(`https://simple-blog-api.crew.red/posts/${id}`);
    const post = res.data;

    return { post };
};

export default Post;
