/* eslint-disable react/react-in-jsx-scope */
/* eslint-disable require-jsdoc */
import axios from 'axios';
import Layout from '../../components/MyLayout';

interface SendPost {
    title: string | undefined;
    body: string | undefined;
}

interface EventSubmith {
    preventDefault(): void;
    target?: {
        elements: {
            title?: {
                type: object | undefined;
                value?: string;
            };
            body?: {
                value?: string;
            };
        };
    };
}

function sendPost(post: SendPost): void {
    axios.post('https://simple-blog-api.crew.red/posts/', {
        title: post.title,
        body: post.body,
    });
}

const newPost = (): object => {
    function handleSubmith(e: EventSubmith): void {
        e.preventDefault();
        const title = e.target.elements.title.value;
        const post = {
            title: e.target.elements.title.value,
            body: e.target.elements.body.value,
        };
        sendPost(post);
    }
    return (
        <Layout>
            <h1>Create new post</h1>
            <form
                action=""
                method="post"
                onSubmit={(e: EventSubmith): void => {
                    handleSubmith(e);
                }}
            >
                <label htmlFor="title">
                    Post Title
                    <br />
                    <input type="text" name="title" />
                </label>
                <br />
                <label htmlFor="body">
                    Post body
                    <br />
                    <textarea name="body" />
                </label>
                <br />
                <button type="submit">submith</button>
            </form>
        </Layout>
    );
};

export default newPost;
