/* eslint-disable react/react-in-jsx-scope */
/* eslint-disable require-jsdoc */
import axios from 'axios';
import Layout from '../../components/MyLayout';
import React from 'react';

interface SendPost {
    title: object | string | undefined;
    body: object | string | undefined;
}

// interface EventSubmit {
//     preventDefault(): void;
//     target: {
//         elements: {
//             title: {
//                 value: string;
//             };
//             body: {
//                 value: string;
//             };
//         };
//     };
// }

function sendPost(post: SendPost): void {
    axios.post('https://simple-blog-api.crew.red/posts/', {
        title: post.title,
        body: post.body,
    });
}

const newPost = (): object => {
    let body: string;
    let title: string;
    function handleBodyChange(e: React.ChangeEvent<HTMLTextAreaElement>): void {
        body = e.target.value;
        console.log('body: ', body);
    }
    function handleTitleChange(e: React.ChangeEvent<HTMLInputElement>): void {
        title = e.target.value;
        console.log('title: ', title);
    }
    function handleSubmit(e: React.FormEvent<HTMLFormElement>): void {
        e.preventDefault();
        const data = e.target;
        console.log(e.target);
        const post = {
            title,
            body,
        };
        console.log(post);
        // sendPost(post);
    }
    return (
        <Layout>
            <h1>Create new post</h1>
            <form
                action=""
                method="post"
                onSubmit={(e): void => {
                    handleSubmit(e);
                }}
            >
                <label htmlFor="title">
                    Post Title
                    <br />
                    <input
                        type="text"
                        name="title"
                        onChange={e => {
                            handleTitleChange(e);
                        }}
                    />
                </label>
                <br />
                <label htmlFor="body">
                    Post body
                    <br />
                    <textarea
                        name="body"
                        onChange={e => {
                            handleBodyChange(e);
                        }}
                    />
                </label>
                <br />
                <button type="submit">submith</button>
            </form>
        </Layout>
    );
};

export default newPost;
