import Layout from '../../components/MyLayout.js';
import axios from 'axios';

const Post = ({ post }) => (
  <Layout>
    <h1>{post.title}</h1>
    <p>{post.body}</p>
  </Layout>
);

Post.getInitialProps = async function (context) {

  const { id } = context.query;
  const res = await axios.get(`https://simple-blog-api.crew.red/posts/${id}`);
  const post = res.data;

  return { post };
}

export default Post;
